#region ____ S Q L DB 생성
/*
USE[MES]
SET ANSI_NULLS ON
SET QUOTED_IDENTIFIER ON
-- =============================================
-- Author:		< Author,,Name >
 --Create date: < Create Date,,>
 --Description:	< Description,,>
 -- =============================================
 CREATE FUNCTION[dbo].[FN_TopDown](@ITEMCODE NVARCHAR(11))
RETURNS @rTable TABLE (
	[PLANTCODE]     nvarchar(50), 
	[ITEMCODE]      NVARCHAR(50),
	[BASEQTY]       NVARCHAR(50),
	[UNITCODE]      NVARCHAR(50),
	[COMPONENT]     NVARCHAR(50),
	[COMPONENTQTY]  NVARCHAR(50),
	[COMPONENTUNIT] NVARCHAR(50),
	[USEFLAG]       NVARCHAR(50),
	[CREATE_DT]     NVARCHAR(50),
	[CREATE_USERID] NVARCHAR(50),
	[MODIFY_DT]     NVARCHAR(50),
	[MODIFY_USERID] NVARCHAR(50)
	--시작
)
AS
BEGIN

WITH OrgTree(PLANTCODE, ITEMCODE, BASEQTY, UNITCODE, COMPONENT, COMPONENTQTY, COMPONENTUNIT, USEFLAG, CREATE_DT, CREATE_USERID, MODIFY_DT, MODIFY_USERID) AS(
		 SELECT PLANTCODE, ITEMCODE, BASEQTY, UNITCODE, COMPONENT, COMPONENTQTY, COMPONENTUNIT, USEFLAG, CREATE_DT, CREATE_USERID, MODIFY_DT, MODIFY_USERID
 
		 FROM TB_BOM
 
		 WHERE ITEMCODE = @ITEMCODE
 

		 UNION ALL
 

		 SELECT A.PLANTCODE, A.ITEMCODE, A.BASEQTY, A.UNITCODE, A.COMPONENT, A.COMPONENTQTY, A.COMPONENTUNIT, A.USEFLAG, A.CREATE_DT, A.CREATE_USERID, A.MODIFY_DT, A.MODIFY_USERID
 
		 FROM TB_BOM AS A
 
		 INNER JOIN OrgTree AS ORG
 
			 ON A.ITEMCODE = ORG.COMPONENT
	 )

	INSERT @rTable
    SELECT PLANTCODE, ITEMCODE, BASEQTY, UNITCODE, COMPONENT, COMPONENTQTY, COMPONENTUNIT, USEFLAG, CREATE_DT, CREATE_USERID, MODIFY_DT, MODIFY_USERID
    FROM OrgTree
    OPTION (MAXRECURSION 100);

RETURN
END
