-- Create date: 2021 - 09 - 14
-- Description: BOM 트리 생성
-- =============================================
CREATE PROCEDURE[dbo].[BOM_Tree]



AS
BEGIN
; with cte as(
select
        distinct ITEMCODE item
       ,0 depth
       , 1 level
       , convert(varchar(100), ITEMCODE )sortorder
 from TB_BOM
 where ITEMCODE not in (select distinct COMPONENT from TB_BOM)

 union all

 select
       COMPONENT item
    , cte.depth+1
    , cte.level+1
    , convert(varchar(100), cte.sortorder + '///' + COMPONENT )
 from cte   inner join TB_BOM b
 on cte.item = b.ITEMCODE
 )
select
	replicate('-', depth) +item item, depth, level
from cte
order by sortorder

END
GO
USE [master]
ALTER DATABASE [MES] SET READ_WRITE
